import { ReactNode, ReactElement } from 'react';
import { INotification } from '../types';
import { SystemColorsType, TypographyColorsType, BaseColorsType } from '../../../types/base';
export interface ICustomInlineNotification extends INotification {
    /** Задает цвет компонента
     * @default  "info" */
    color?: SystemColorsType | BaseColorsType;
    /** Задает иконку */
    icon?: ReactNode;
    /** Задает цвет заголовока
     *  @default "main" */
    titleColor?: TypographyColorsType;
    /** Задает кнопки уведомления */
    actionButtons?: ReactElement[];
}
